mapper.insert:
INSERT INTO ADDRESS_BOOK_LIST (
		LIST_PK
		,BOOK_OWNER_ID
		,BOOK_NAME		

) 
VALUES(
		(SELECT 'LIST_' ||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(LIST_PK,6))),0) +1,3,0) FROM ADDRESS_BOOK_LIST)
		,#{bookOwnerId}
		,#{bookName}
)

INSERT INTO ADDRESS_BOOK (
			BOOK_PK
			,LIST_PK
			,EMP_NAME
			,EMP_TELL
			,EMP_ADDR
			,EMP_EMAIL
			,DEPT_NAME
			,EMP_POSITION
) 
VALUES (
		(SELECT 'BOOK_' ||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(BOOK_PK,6))),0) +1,3,0) FROM ADDRESS_BOOK)
		,(SELECT LIST_PK 
				FROM 
				ADDRESS_BOOK_LIST 
				WHERE LIST_PK = #{listPk})
		,#{empName}
		,#{empTell}
		,#{empAddr}
		,#{empEmail}
		,#{deptName}
		,#{empPosition}
)

mapper.update:
UPDATE ADDRESS_BOOK
		SET
		EMP_NAME = #{empName}
		,EMP_TELL = #{empTell}
		,EMP_ADDR = #{empAddr}
		,EMP_EMAIL = #{empEmail}
		,DEPT_NAME = #{deptName}
		,EMP_POSITION = #{empPosition}

		WHERE BOOK_PK = #{bookPk}
