mapper.insert:
		INSERT INTO APPROVAL(
			APP_SEQ
            ,USER_ID
            ,TITLE
            ,APP_CATE_CODE
		) VALUES(
			(SELECT 'APP_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(APP_SEQ, 5))), 0) + 1, 3, 0) FROM APPROVAL)
            ,#{userId}
            ,#{title}
            ,#{appCateCode}
		)


	SELECT 'VACATION_APP_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(VACATION_SEQ, 14))), 0) + 1, 3, 0) 
	FROM APP_FORM_VACATION  

INSERT INTO APP_FORM_VACATION (
	APP_SEQ
	, VACATION_SEQ
	, VACATION_START_DATE
	, VACATION_END_DATE
	, VACATION_PERIOD_DATE
	, VACATION_CONTENT
	, USER_ID
)VALUES (
	#{appSeq}
	, #{vacationSeq}
	, #{vacationStartDate}
	, #{vacationEndDate}
	, #{vacationPeriodDate}
	, #{vacationContent}
	, #{userId}
)


	SELECT 'NOMAL_APP_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(NOMAL_SEQ, 11))), 0) + 1, 3, 0) 
	FROM APP_FORM_NOMAL  

INSERT INTO APP_FORM_NOMAL (
	APP_SEQ
	, NOMAL_SEQ
	, NOMAL_CONTENT
	, USER_ID
)VALUES (
	#{appSeq}
	, #{nomalSeq}
	, #{nomalContent}
	, #{userId}
)


	SELECT 'ACCOUNTING_APP_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(ACCOUNTING_SEQ, 16))), 0) + 1, 3, 0) 
	FROM APP_FORM_ACCOUNTING  

INSERT INTO APP_FORM_ACCOUNTING (
	ACCOUNTING_SEQ
	, PAYMENT_REQUEST_DATE
	, ACCOUNTING_CLIENT
	, ACCOUNT_NUMBER
	, ACCOUNT
	, BUSINESS_REGISTRATION_NUMBER
	, AMOUNT
	, ACCOUNTING_CONTENT
	, ACCOUNTING_CATEGORY
	, APP_SEQ
	, USER_ID
)VALUES (
	#{accountingSeq}
	, #{paymentRequestDate}
	, #{accountingClient}
	, #{accountNumber}
	, #{account}
	, #{businessRegistrationNumber}
	, #{amount}
	, #{accountingContent}
	, #{accountingCategory}
	, #{appSeq}
	, #{userId}
)


	SELECT 'RECEIVE_REF_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(RECEIVE_REF_NUM, 13))), 0) + 1, 3, 0) 
	FROM APP_RECEIVE_REF  

INSERT INTO APP_RECEIVE_REF(
	RECEIVE_REF_NUM
	, APP_SEQ
	, FIRST_APPROVAL
	, FIRST_APPROVAL_EMP
	, FINAL_APPROVAL
	, FINAL_APPROVAL_EMP
	, APP_CATE_CODE
	, APP_SEQ_STATUS
) VALUES(
	#{receiveRefNum}
	, #{appSeq}
	, #{firstApproval}
	, #{firstApprovalEmp}
	, #{finalApproval}
	, #{finalApprovalEmp}
	, #{appCateCode}
	, #{appSeqStatus}
)

mapper.select:
SELECT APPROVAL.APP_SEQ , APP_WRITE_DATE , APPROVAL.APP_CATE_CODE , TITLE , APPROVAL.USER_ID ,(SELECT EMP_NAME FROM EMP WHERE EMP.USER_ID = APPROVAL.USER_ID) AS EMP_NAME ,(SELECT DEPT_NAME FROM EMP WHERE EMP.USER_ID = APPROVAL.USER_ID) AS DEPT_NAME FROM APPROVAL , APP_CATEGORY CATEGORY , EMP WHERE APPROVAL.APP_CATE_CODE = CATEGORY.APP_CATE_CODE AND APPROVAL.USER_ID = EMP.USER_ID
AND APPROVAL.APP_SEQ = #{app_seq}
AND UPPER(TITLE) LIKE UPPER('%'||#{title}||'%')
AND EMP_NAME LIKE ('%'||#{empName}||'%')
AND APPROVAL.APP_CATE_CODE = #{appCateCode}
AND TO_CHAR(APP_WRITE_DATE, 'YYYY-MM-DD') >= #{fromDate}
AND TO_CHAR(APP_WRITE_DATE, 'YYYY-MM-DD') <= #{toDate}
ORDER BY APPROVAL.APP_SEQ DESC

SELECT * 
FROM APPROVAL APP, EMP , ${table}
WHERE  APP.APP_SEQ = ${table}.APP_SEQ
AND APP.USER_ID = EMP.USER_ID
AND APP.APP_SEQ = #{appSeq}
