mapper.insert:
		INSERT INTO APPROVAL(
			APP_SEQ
            ,USER_ID
		) VALUES(
			(SELECT 'APP_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(APP_SEQ, 5))), 0) + 1, 3, 0) FROM APPROVAL)
            ,#{userId}
		)


	SELECT 'VACATION_APP_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(VACATION_SEQ, 14))), 0) + 1, 3, 0) 
	FROM APP_FORM_VACATION  

INSERT INTO APP_FORM_VACATION (
	APP_SEQ
	, VACATION_SEQ
	, VACATION_START_DATE
	, VACATION_END_DATE
	, VACATION_PERIOD_DATE
	, VACATION_CONTENT
	, USER_ID
)VALUES (
	#{appSeq}
	, #{vacationSeq}
	, #{vacationStartDate}
	, #{vacationEndDate}
	, #{vacationPeriodDate}
	, #{vacationContent}
	, #{userId}
)


	SELECT 'NOMAL_APP_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(NOMAL_SEQ, 11))), 0) + 1, 3, 0) 
	FROM APP_FORM_NOMAL  

INSERT INTO APP_FORM_NOMAL (
	APP_SEQ
	, NOMAL_SEQ
	, NOMAL_TITLE
	, NOMAL_CONTENT
	, USER_ID
)VALUES (
	#{appSeq}
	, #{nomalSeq}
	, #{nomalTitle}
	, #{nomalContent}
	, #{userId}
)


	SELECT 'ACCOUNTING_APP_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(ACCOUNTING_SEQ, 16))), 0) + 1, 3, 0) 
	FROM APP_FORM_ACCOUNTING  

INSERT INTO APP_FORM_ACCOUNTING (
	ACCOUNTING_SEQ
	, PAYMENT_REQUEST_DATE
	, ACCOUNTING_CLIENT
	, ACCOUNT_NUMBER
	, ACCOUNT
	, BUSINESS_REGISTRATION_NUMBER
	, AMOUNT
	, ACCOUNTING_TITLE
	, ACCOUNTING_CONTENT
	, ACCOUNTING_CATEGORY
	, APP_SEQ
	, USER_ID
)VALUES (
	#{accountingSeq}
	, #{paymentRequestDate}
	, #{accountingClient}
	, #{accountNumber}
	, #{account}
	, #{businessRegistrationNumber}
	, #{amount}
	, #{accountingTitle}
	, #{accountingContent}
	, #{accountingCategory}
	, #{appSeq}
	, #{userId}
)
