mapper.insert:
INSERT INTO BOARD_REPLY(
	REPLY_NUM
	, REPLY_CONTENT
	, BOARD_NUM
	, USER_ID
) VALUES(
	(SELECT NVL(MAX(REPLY_NUM),0)+1 FROM BOARD_REPLY)
	, #{replyContent}
	, #{boardNum}
	, #{userId}
)

mapper.update:
UPDATE BOARD_REPLY
SET REPLY_CONTENT = #{replyContent}
WHERE REPLY_NUM = #{replyNum}
